import React, { useState } from 'react';

function ImageUpload() {
  const [image, setImage] = useState(null);
  const [prediction, setPrediction] = useState('');
  const [imageToShow, setImageToShow] = useState(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageToShow(URL.createObjectURL(file));
      setImage(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!image) return;

    const formData = new FormData();
    formData.append('file', image);

    try {
      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (data.especieInfo) {
        setPrediction(data.especieInfo.nombre); // Asegúrate de que esto coincida con la estructura de datos enviada desde Flask
      } else {
        setPrediction('Predicción no disponible');
      }
    } catch (error) {
      console.error('Error al enviar la imagen:', error);
      setPrediction('Error al procesar la imagen');
      setImageToShow(null); // En caso de error, no mostrar imagen
    }
  };  }
