import React, { useState } from 'react';

function ImageUpload() {
  const [image, setImage] = useState(null);
  const [prediction, setPrediction] = useState('');
  const [imageToShow, setImageToShow] = useState(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageToShow(URL.createObjectURL(file));
      setImage(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!image) return;

    const formData = new FormData();
    formData.append('file', image);

    try {
      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      setPrediction(data.prediccion);
      // No mostrar la imagen hasta recibir la predicción
    } catch (error) {
      console.error('Error al enviar la imagen:', error);
      setImageToShow(null); // En caso de error, no mostrar imagen
    }
  };

  return (
    <div className="image-upload-container">
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleImageChange} />
        <button type="submit">Enviar Imagen</button>
      </form>
      {prediction && (
        <>
          <p>Predicción: {prediction}</p>
          {imageToShow && <img src={imageToShow} alt="Imagen subida" style={{ maxWidth: '300px', maxHeight: '300px' }} />}
        </>
      )}
    </div>
  );
}

export default ImageUpload;
